    public static String seperate(int number) {//p1 and p2 seperator
        if (number == 0) {
            return "    ";
        } else if (number > 0) {
            return " " + (number) + "X ";//p1
        } else {
            return " " + Math.abs(number) + "Y ";//p2
        }
    }

