    public static boolean collectFlakeX(int[] flake) {//can p1 collect flake 
        int sum = 0;
        for (int i = 0; i <= 17; i++) {
            if (flake[i] > 0) {
                sum = flake[i] + sum;
            }
        }

        if (sum > 0) {
            return false;
        } else {
            return true;
        }
    }

    public static boolean collectFlakeY(int[] flake) {//can p1 collect flake 
        int sum = 0;
        for (int i = 23; i >= 6; i--) {
            if (flake[i] < 0) {
                sum = flake[i] + sum;
            }
        }

        if (sum < 0) {
            return false;
        } else {
            return true;
        }
    }

